package com.example.task.ui.fragment.boardFragment;

import android.annotation.SuppressLint;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.Button;
import android.widget.ImageView;
import android.widget.TextView;

import androidx.annotation.NonNull;
import androidx.recyclerview.widget.RecyclerView;

import com.bumptech.glide.Glide;
import com.example.task.R;
import com.squareup.picasso.Picasso;

public class AdapterForViewPager extends RecyclerView.Adapter<AdapterForViewPager.ViewHolder> {
    private String [] image1 = {
       "data:image/jpeg;base64,",
            "https://img1.liveinternet.ru/images/attach/c/5/89/11/89011453_smeshnuye_kotyata_foto_8.jpg",
            "https://zooclub.ru/attach/dogs/0908.jpg",
    "https://www.bethowen.ru/upload/iblock/3cc/3cc0e2305bb62234b32678027ef738ea.jpg"};
    private String [] title = {
            "факт о пандах",
            "котики это топ",
            "почему щеночки такие милые",
            "почему хомяк и Иисус одно и тоже"};
    private String [] desc = {
            "Китайское имя панды (сюнмао) означает «медведь-кошка», а латинское название Ailuropoda melanoleuca - можно перевести как «котолап черно-белый».",
            "Самцы и самки представителей кошачьих переживают сложные ситуации по-разному",
            "Эволюция человека привела к появлению у собак более выразительного взгляда. Как выяснили ученые из британского Портсмутского университета, у псов на протяжении миллиона лет развилась специальная мышца, которая формирует знаменитый  взгляд, от которого тают сердца многих людей",
            "Собак заводят те кто хочет чтоб их любили, кошек те кто готов любить, а хомяка те кто хочет показать детям смерть)"};

    @NonNull
    @Override
    public AdapterForViewPager.ViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {
        return new ViewHolder(LayoutInflater.from(parent.getContext()).
                inflate(R.layout.item_for_view_pager, parent, false));
    }

    @Override
    public void onBindViewHolder(@NonNull AdapterForViewPager.ViewHolder holder, int position) {
        holder.onBind(image1[position], title[position], desc[position]);
    }

    @Override
    public int getItemCount() {
        return image1.length;
    }

    public static class ViewHolder extends RecyclerView.ViewHolder {
        private final ImageView image;
        private final TextView title;
        private final TextView desc;
        @SuppressLint("CutPasteId")
        public ViewHolder(@NonNull View itemView) {
            super(itemView);
            image = itemView.findViewById(R.id.img_from_item_for_view_pager);
            title = itemView.findViewById(R.id.txt_item_vp);
            desc = itemView.findViewById(R.id.txt_item_decs_vp);

        }

        public void onBind(String image, String title, String desc) {
            Glide.with(this.image).load(image).
                    into(this.image);
            this.title.setText(title);
            this.desc.setText(desc);
        }
    }
}
